// 导入所有军理知识点的函数
function importAllMilitaryKnowledge() {
    try {
        // 构建完整的知识库结构
        const completeKnowledgeBase = {
            ...militaryKnowledgeComplete,
            id: 'military_theory_base'  // 设置固定的知识库ID
        };
        
        // 将知识点扁平化用于复习系统
        const allKnowledge = completeKnowledgeBase.areas.reduce((acc, area) => {
            return acc.concat(area.knowledgePoints.map(item => ({
                ...item,
                category: area.name,
                areaId: area.id,
                areaColor: area.color,
                baseId: completeKnowledgeBase.id  // 添加知识库ID
            })));
        }, []);
        
        // 获取现有数据
        const existingData = JSON.parse(localStorage.getItem('memorin_data') || '{}');
        
        // 保存知识库结构
        existingData.knowledgeBase = completeKnowledgeBase;
        
        // 替换为完整的军理知识点
        existingData.knowledge = allKnowledge.map(item => ({
            ...item,
            reviewCount: 0,
            correctCount: 0,
            nextReview: new Date().toISOString(),
            interval: 1,
            easeFactor: 2.5,
            createdAt: new Date().toISOString(),
            updatedAt: new Date().toISOString()
        }));
        
        // 清空错题和复习历史
        existingData.mistakes = [];
        existingData.reviewHistory = [];
        
        // 重置统计数据
        existingData.statistics = {
            totalReviews: 0,
            correctAnswers: 0,
            totalQuestions: allKnowledge.length,
            studyTime: 0,
            streakDays: 0,
            lastStudyDate: null
        };
        
        // 保存到localStorage
        localStorage.setItem('memorin_data', JSON.stringify(existingData));
        
        console.log(`成功导入${allKnowledge.length}个军理知识点！`);
        alert(`成功导入${allKnowledge.length}个军理知识点！\n包含：\n- 第一章：国防概述 (27个)\n- 第二章：国家安全 (5个)\n- 第三章：军事思想 (5个)\n- 第四章：现代战争 (7个)\n- 第五章：信息化装备 (10个)\n\n请刷新页面查看。`);
        
        // 刷新页面
        window.location.reload();
        
    } catch (error) {
        console.error('导入军理知识点失败：', error);
        alert('导入失败，请检查控制台错误信息');
    }
}

// 如果在浏览器环境中，提供全局函数
if (typeof window !== 'undefined') {
    window.importAllMilitaryKnowledge = importAllMilitaryKnowledge;
    window.militaryKnowledgeComplete = militaryKnowledgeComplete;
} 